import React from 'react';

const Editor = ({markdownText, handleChange}) => {
    return (
        <div>
            <h3>Mardown Editor</h3>
            <textarea value={markdownText} handleChange={handleChange}></textarea>
        </div>
    );
};

export default Editor;
