import defaultMarkdownText from "./get-mardown-text";

const initialState = {
    markdownText: '#title',
};
  
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_MARKDOWN_TEXT':
            return { ...state, markdownText: action.payload };
        default:
            return state;
    }
};
  
export default reducer;
