const fs = require('fs');

// Specify the file path
const filePath = 'public\\mardown-text.txt';

let defaultMarkdownText = '';

// Read the file
fs.readFile(filePath, 'utf8', (err, data) => {
  if (err) {
    console.error(err);
    return;
  }

  // Store the contents of the file in a variable
  defaultMarkdownText = data;

  // Use the fileContents variable as needed
  // console.log(defaultMarkdownText);
});

export default defaultMarkdownText;
